Ext.define('dalpeApp.controller.global', {
    extend: 'Ext.app.Controller',

    onViewportAfterRender: function(component, eOpts) {
        Employes.checkLoginSession(function(response){
            if (response) {
                user_logged = response[0].id;
                var myAppToolbar = Ext.getCmp('appToolbar');
                myAppToolbar.down('#userLogged').setText('Utilisateur connect√©: ' + response[0].prenom + " " + response[0].nom);
            }
            else {
                //Ext.widget('loginWindow');
            }
        }, this);
    },

    onLogoutButtonClick: function(button, e, eOpts) {
        Employes.logout(function(){
            Ext.widget('loginWindow');
        }, this);
    },

    init: function(application) {
        this.control({
            "#appViewport": {
                afterrender: this.onViewportAfterRender
            },
            "#appViewport #logout": {
                click: this.onLogoutButtonClick
            }
        });
    }

});
